import React, { useEffect, useState } from 'react'
import { fetchMovies } from '../../services/api'
import MovieCard from '../../molecules/MovieCard'
import { Movie } from '../../interfaces/Movie' // Importe a interface Movie conforme necessário

const MovieList: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const getMovies = async () => {
      try {
        const data = await fetchMovies()
        setMovies(data.products)
        setLoading(false)
      } catch (error) {
        console.error('Error fetching movies:', error)
        setLoading(false)
      }
    }

    getMovies()
  }, [])

  const handleAddToCart = (id: number) => {
    // Lógica para adicionar ao carrinho
    console.log(`Adicionando filme com ID ${id} ao carrinho`)
  }

  const handleRemoveFromCart = (id: number) => {
    // Lógica para remover do carrinho
    console.log(`Removendo filme com ID ${id} do carrinho`)
  }

  if (loading) {
    return <p>Loading...</p>
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {movies.map((movie) => (
        <MovieCard
          key={movie.id}
          id={movie.id}
          title={movie.title}
          price={movie.price}
          image={movie.image}
          handleAddToCart={() => handleAddToCart(movie.id)}
          onRemoveFromCart={() => handleRemoveFromCart(movie.id)}
          quantity={0} // Inicialmente, a quantidade é zero
        />
      ))}
    </div>
  )
}

export default MovieList
