import React from 'react'
import { Movie } from '../../interfaces/Movie'

interface MovieCardProps {
  id: number
  title: string
  price: number
  image: string
  onAddToCart: () => void
  onRemoveFromCart: () => void
  quantity?: number // tornando quantity opcional tambÃ©m aqui
}

const MovieCard: React.FC<MovieCardProps> = ({
  id,
  title,
  price,
  image,
  onAddToCart,
  onRemoveFromCart,
  quantity = 0, // definindo um valor padrÃ£o para quantity caso nÃ£o seja fornecido
}) => {
  const handleAddToCart = () => {
    onAddToCart() // chamando a funÃ§Ã£o recebida por props
  }

  const handleRemoveFromCart = () => {
    onRemoveFromCart() // chamando a funÃ§Ã£o recebida por props
  }

  return (
    <div className="bg-white p-4 rounded-lg shadow-lg text-center">
      <img src={image} alt={title} className="w-full h-64 object-cover mb-4 mx-auto" />
      <h2 className="text-lg font-bold mb-2">{title}</h2>
      <p className="text-gray-700 mb-4">${price.toFixed(2)}</p>
      <div className="flex justify-center items-center">
        <button
          onClick={handleAddToCart}
          className={`px-4 py-2 rounded-l text-white flex items-center justify-center ${
            quantity > 0 ? 'bg-green-500' : 'bg-blue-500'
          }`}
        >
          <span className="mr-2">ðŸ›’</span>
          <span>{quantity > 0 ? `${quantity}` : 'Adicionar ao carrinho'}</span>
        </button>
        {quantity > 0 && (
          <button onClick={handleRemoveFromCart} className="px-4 py-2 rounded-r text-white bg-red-500">
            Remover
          </button>
        )}
      </div>
    </div>
  )
}

export default MovieCard
