import React, { useState } from 'react'
import { MovieCardProps } from './props'

const MovieCard: React.FC<MovieCardProps> = ({ id, title, price, image }) => {
  const [quantity, setQuantity] = useState(0)
  const [isAdded, setIsAdded] = useState(false)

  const handleAddToCart = () => {
    if (!isAdded) {
      setQuantity(quantity + 1)
      setIsAdded(true)
    }
  }

  return (
    <div className="bg-white p-4 rounded-lg shadow-lg text-center">
      <img src={image} alt={title} className="w-full h-64 object-cover mb-4 mx-auto" />
      <h2 className="text-lg font-bold mb-2">{title}</h2>
      <p className="text-gray-700 mb-4">${price.toFixed(2)}</p>
      <button
        onClick={handleAddToCart}
        className={`px-4 py-2 rounded text-white flex items-center justify-center ${
          quantity > 0 ? 'bg-green-500' : 'bg-blue-500'
        } h-12 w-full sm:w-auto`}
        disabled={isAdded}
      >
        <span className="mr-2">ðŸ›’</span>
        {quantity > 0 ? `${quantity}` : ''}
        <span className="ml-2">{isAdded ? 'Adicionado' : 'Adicionar ao carrinho'}</span>
      </button>
    </div>
  )
}

export default MovieCard
