import React, { useEffect, useState } from 'react'
import { fetchMovies } from '../../services/api'
import MovieCard from '../../molecules/MovieCard'
import { Movie } from '../../interfaces/Movie' // Importe a interface Movie
import Loader from '../../atoms/Loader'

interface MovieListProps {
  onAddToCart: (movie: Movie) => void
  onRemoveFromCart: (movie: Movie) => void
  cartItems: Movie[]
}

const MovieList: React.FC<MovieListProps> = ({ onAddToCart, onRemoveFromCart, cartItems }) => {
  const [movies, setMovies] = useState<Movie[]>([])
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const getMovies = async () => {
      try {
        const data = await fetchMovies()
        setMovies(data.products)
        setLoading(false)
      } catch (error) {
        console.error('Error fetching movies:', error)
        setLoading(false)
      }
    }

    getMovies()
  }, [])

  if (loading) {
    return <Loader />
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {movies.map((movie) => {
        const cartItem = cartItems.find((item) => item.id === movie.id)
        const quantity = cartItem ? cartItem.quantity ?? 0 : 0 // Garantir que quantity é sempre um número
        return (
          <MovieCard
            key={movie.id}
            id={movie.id}
            title={movie.title}
            price={movie.price}
            image={movie.image}
            onAddToCart={() => onAddToCart(movie)}
            onRemoveFromCart={() => onRemoveFromCart(movie)}
            quantity={quantity}
          />
        )
      })}
    </div>
  )
}

export default MovieList
