import React from 'react'
import { BreadcrumbProps, BreadcrumbRoute } from './types'

function Breadcrumb({ routes, currentPath }: BreadcrumbProps) {
  const selectedStyle = 'font-bold text-underline'

  return (
    <div className="flex gap-2">
      {routes.map(({ name, path }, index) => (
        <div key={path} className="flex gap-2">
          <span className={`${currentPath === path ? selectedStyle : ''}`}>
            {index > 0 && ' > '}
            {currentPath === path ? <u>{name}</u> : name}
          </span>
        </div>
      ))}
    </div>
  )
}

export default Breadcrumb
