import React, { useState } from 'react'
import Header from '../../organisms/Header'
import MovieList from '../../organisms/MovieList'
import Cart from '../../organisms/Cart'
import { Movie } from '../../interfaces/Movie'

const Home: React.FC = () => {
  const [cartItems, setCartItems] = useState<Movie[]>([])
  const [showCart, setShowCart] = useState(false)

  const handleAddToCart = (movie: Movie) => {
    // Verificar se o filme já está no carrinho
    const existingItemIndex = cartItems.findIndex((item) => item.id === movie.id)

    if (existingItemIndex !== -1) {
      // Se o filme já estiver no carrinho, criar uma cópia do array e atualizar a quantidade
      const updatedCartItems = [...cartItems]
      updatedCartItems[existingItemIndex] = {
        ...updatedCartItems[existingItemIndex],
        quantity: (updatedCartItems[existingItemIndex].quantity || 0) + 1,
      }
      setCartItems(updatedCartItems)
    } else {
      // Se o filme não estiver no carrinho, adicionar com quantidade inicial 1
      setCartItems([...cartItems, { ...movie, quantity: 1 }])
    }
  }

  const handleRemoveFromCart = (movie: Movie) => {
    // Verificar se o filme está no carrinho
    const existingItemIndex = cartItems.findIndex((item) => item.id === movie.id)

    if (existingItemIndex !== -1 && (cartItems[existingItemIndex].quantity || 0) > 0) {
      // Se o filme estiver no carrinho e tiver quantidade maior que 0, criar uma cópia do array e atualizar a quantidade
      const updatedCartItems = [...cartItems]
      updatedCartItems[existingItemIndex] = {
        ...updatedCartItems[existingItemIndex],
        quantity: (updatedCartItems[existingItemIndex].quantity || 0) - 1,
      }
      setCartItems(updatedCartItems.filter((item) => item.quantity && item.quantity > 0)) // Remover itens com quantidade zero
    }
  }

  const handleRemoveAllFromCart = () => {
    setCartItems([])
    setShowCart(false)
  }

  const handleFinalizePurchase = () => {
    // Implemente a lógica para finalizar a compra
    console.log('Compra finalizada!')
  }

  return (
    <div className="bg-custom-color text-white min-h-screen flex flex-col">
      <Header />
      <main className="flex flex-1 justify-center items-center p-4">
        {!showCart ? (
          <MovieList onAddToCart={handleAddToCart} onRemoveFromCart={handleRemoveFromCart} />
        ) : (
          <Cart items={cartItems} onRemoveAll={handleRemoveAllFromCart} onFinalizePurchase={handleFinalizePurchase} />
        )}
      </main>
      <button
        className="fixed bottom-4 right-4 bg-blue-500 text-white px-4 py-2 rounded"
        onClick={() => setShowCart(!showCart)}
      >
        {showCart ? 'Fechar Carrinho' : 'Abrir Carrinho'}
      </button>
    </div>
  )
}

export default Home
